import React, { Component } from 'react';
import { Button, Modal } from 'react-bootstrap';
import PageHeader from './pageheader.js'
import '../styles/registration.scss'



class RegistrationForm extends Component {
  constructor(props) {
    super(props);
    this.state = {
      showForm: false,
    };
  }

  toggleShowForm = () => {
    this.setState(prevState => ({
      showForm: !prevState.showForm,
    }));
    console.log(this.state.showForm);
  }

  showFormModal = () => {
    return (
      <Modal className="absentee-modal-form" id="absentee-mod-content" show={this.state.showForm} onHide={this.toggleShowForm} centered>
        <Modal.Header className="absentee-modal-header" id="absentee-mod-head">
          <Modal.Title id="absentee-mod-title">Fill Out Application</Modal.Title>
        </Modal.Header>
        <Modal.Body className="absentee-modal-body">
          <form id="complete-application" onSubmit={this.handleSubmit}>
            <p>
              Reward Name:
              <input className="absentee-form-labels" type="text" name="name" />
            </p>
            <p>
              Reward Points:
              <input className="absentee-form-labels" type="text" name="points" />
            </p>
            <p>
              Amount Available:
              <input className="absentee-form-labels" type="text" name="amount" />
            </p>
            <p>
              Description:
              <input className="absentee-form-labels" type="text" name="description" />
            </p>
          </form>
        </Modal.Body>
        <Modal.Footer className="absentee-modal-footer">
          <Button className="absentee-modal-button" onClick={() => this.toggleShowForm()}>Cancel</Button>
          <Button className="absentee-modal-button" type="submit" onClick={() => this.handleSubmit()}>Add Reward</Button>
        </Modal.Footer>
      </Modal>
    );
  }


  render() {
    return (
      <div className="registration-form-page">
          <PageHeader></PageHeader>
          <p>Click the following button to fill out an absentee ballot! Note that once the form is complete, the application will automatically
          be submitted, and you will receive a ballot at your given address</p>
          <Button className="show-form-button" onClick={() => this.toggleShowForm()}>Complete Form</Button>
          {this.showFormModal()}
      </div>
    );
  }
}

export default RegistrationForm
